aws:cdk:cloudformation:props.definition:
schema {
    query: Query
    mutation: Mutation
}

type post {
    id: ID!
    title: String!
    content: String
    author: String
    createdAt: AWSDateTime
}

type comment {
    postId: String!
    commentId: ID!
    comment: String
    author: String
    createdAt: AWSDateTime
}

type Query {
    listPost: [ post ]
    getPost(id: ID!): post
    listComment(postId: String!): [ comment ]
}

type Mutation {
    createPost(input: createPostInput!): post
    createComment(input: createCommentInput!): comment
}

input createCommentInput {
    postId: String!
    comment: String!
}

input createPostInput {
    title: String!
    content: String
}

aws:cdk:cloudformation:props.requestMappingTemplate:
#set($id=$utils.autoId())
#set($now=$util.time.nowISO8601())
{
    "version": "2018-05-29",
    "statements": [
        "INSERT INTO db.post VALUES ('$id', '$ctx.args.input.title', '$ctx.args.input.content', '$now', 'system')",
        "SELECT * FROM db.post WHERE id = '$id'"
    ]
}

{
    "version": "2018-05-29",
    "statements": [
        $util.toJson("SELECT * FROM db.post ORDER BY createdAt DESC LIMIT 10")
    ]
}
